sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var exp = /^(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/;

    if (exp.test(g_form.getValue('ip'))) {
        var ga = new GlideAjax("EnderecoAjax");
        ga.addParam("sysparm_name", "loadDataFromIP");
        ga.addParam("ip", g_form.getValue('ip'));
        ga.getXML(function(data) {
            var result = data.responseXML.getElementsByTagName("result");
            var ip = result[0].getAttribute("ip");
            var cep = result[0].getAttribute("cep");
            g_form.setValue('cep', cep);
        });
    }

}
