sys_script_include.script:
var EnderecoAjax = Class.create();
EnderecoAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    type: 'EnderecoAjax',
    loadDataFromIP: function() {
        var result = this.newItem('result');     
		var ipParam = this.getParameter('ip');		
        var ipStackKey = 'b7608d4de1d02289b73f91a028d7f3e5';

        var request = new sn_ws.RESTMessageV2();
        request.setEndpoint('http://api.ipstack.com/'+ipParam+'?access_key=' + ipStackKey + '&format=1');
        request.setHttpMethod('GET');
        request.setRequestHeader("Accept", "application/json");

		var response = request.execute();
        var httpResponseStatus = response.getStatusCode();

        if (httpResponseStatus == 200) {
			var body = JSON.parse(response.getBody());
            result.setAttribute('ip', body.ip);
			result.setAttribute('cep', body.zip);
        }


    }
});
